#include<bits/stdc++.h>using namespace std;typedef long long ll;void solve(){    ll n,k;    cin>>n>>k;    string s;    cin>>s;    vector<int>marked(n,0);    int cnt=0;    for (int i = 0; i < n; i++)    {        if(s[i]=='1'){            marked[i]++;            if(i+k+1<n)marked[i+k+1]--;        }    }    for (int i = 1; i < n; i++)        marked[i]+=marked[i-1];    for(int i:marked)cnt+=(i==0);        cout<<cnt<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}