#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}// int find(int idx,int curr_score,vector<ll>&a,vector<ll>&b){//     // basecase//     if(idx==a.size())return curr_score;//     int op1=find(idx+1,curr_score-a[idx],a,b);//     int op2=find(idx+1,b[idx]-curr_score,a,b); //     return max(op1,op2);// } void solve(){    ll n;    cin>>n;    vector<ll>a(n),b(n);    f(n,a),f(n,b);    ll curr_low=0,curr_high=0;    for (ll i = 0; i < n; i++)    {        ll a1=curr_low-a[i];        ll a2=b[i]-curr_low;        ll a3=curr_high-a[i];        ll a4=b[i]-curr_high;         curr_low=min({a1,a2,a3,a4});        curr_high=max({a1,a2,a3,a4});    }    cout<<curr_high<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}