#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];} void solve(){    string s,t;    cin>>s>>t;    vector<int>hash_s(26,0),hash_t(26,0);    for(char c:s)hash_s[c-'a']++;    for(char c:t)hash_t[c-'a']++;     for (int i = 0; i < 26; i++)    {        if(hash_s[i]>hash_t[i]){            cout<<"Impossible"<<'\n';            return;        }    }    for (int i = 0; i < s.length(); i++)    {        for (int j = 0; j <s[i]-'a'; j++)        {            ll d=hash_t[j]-hash_s[j];            hash_t[j]-=d;            if(d!=0){                string temp(d,j+'a');                cout<<temp;            }        }        cout<<s[i];        hash_s[s[i]-'a']--,hash_t[s[i]-'a']--;    }    for (int i = 0; i < 26; i++)    {        if(hash_t[i]>0){            string k(hash_t[i],i+'a');            cout<<k;        }    }    cout<<'\n';} int main(){ll t;cin>>t;while (t--){    solve();}     return 0;}