#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll a,b;    cin>>a>>b;     ll mn=min(a,b),mx=max(a,b);     // case 1: start from lo    ll cnt = 0,sum=1,i=1;    while(sum<=mn){        cnt++;        i*=4;        sum+=i;    }     ll cnt2=0;    sum=2;i=2;    while(cnt2<cnt && sum<=mx){        cnt2++;        i*=4;        sum+=i;    }     ll ans=cnt+cnt2;    cnt=0,sum=2,i=2;    while(sum<=mn){        cnt++;        i*=4;        sum+=i;    }     cnt2=0;    sum=1,i=1;    while(cnt2<=cnt && sum<=mx){        cnt2++;        i*=4;        sum+=i;    }     ans=max(ans,cnt+cnt2);    cout<<ans<<'\n';}  int main(){ios::sync_with_stdio(0);cin.tie(0);ll t;cin>>t;while (t--){    solve();}     return 0;}