#include<bits/stdc++.h>using namespace std;typedef long long ll;const int MAXA=100000;int spf[MAXA+1]; int best[MAXA+1];    void build_spf(){    for (int i = 1; i <=MAXA; i++)spf[i]=i;        for (int i = 2; i*i<=MAXA; i++)    {        if(spf[i]==i){            for (int j = i*i; j <=MAXA; j+=i)            if(spf[j]==j)spf[j]=i;        }    }} vector<int>factorize(int x){    vector<int>factors;    while (x>1)    {        int p=spf[x];        while (x%p==0)x/=p;        factors.push_back(p);    }    return factors;}int main(){ios::sync_with_stdio(0);cin.tie(0); build_spf();int n;cin>>n;vector<int>a(n);for (int i = 0; i < n; i++)cin>>a[i];int ans=1;for (int i = 0; i < n; i++){    vector<int>factors=factorize(a[i]);    int curr_len=1;     for(int p:factors)        curr_len=max(curr_len,best[p]+1);        for(int p:factors)       best[p]=max(best[p],curr_len);        ans=max(ans,curr_len);}cout<<ans<<'\n';     return 0;}