#include<bits/stdc++.h>using namespace std;typedef long long ll;const int maxn=5*1e6;int spf[maxn+1];void build_spf(){    for (int i = 0; i <=maxn; i++)spf[i]=i;    for (int i = 2; i*i<=maxn; i++)    {        if(spf[i]==i){            for (int j = i*i; j<=maxn; j+=i)                if(spf[j]==j)spf[j]=i;        }    }}int pf[maxn+1];int pref[maxn+1];void prime_factors_till_i(){    for (int i = 2; i<=maxn; i++)    {        ll x=i;       while (x>1)       {         ll p=spf[x];            pf[i]++;            x/=p;       }       pref[i]=pf[i]+pref[i-1];    }}  void solve(){    ll a,b;    cin>>a>>b;    ll ans=(pref[a]-pref[b]);     cout<<ans<<'\n';   } int main(){ios::sync_with_stdio(0);cin.tie(0);build_spf();prime_factors_till_i();ll t;cin>>t;while (t--){    solve();}     return 0;}