#include<bits/stdc++.h>using namespace std;typedef long long ll;bool isPrime(ll x) {    if (x<2) return false;    if (x==2) return true;    if (x%2==0) return false;    for (ll i=3;i*i<=x; i+=2) {        if (x%i==0) return false;    }    return true;}Â int main(){ios::sync_with_stdio(0);cin.tie(0);    ll n;    cin>>n;    if(isPrime(n))        cout<<1<<'\n';        else if(n&1){        if(isPrime(n-2))cout<<2<<'\n';        else cout<<3<<'\n';    }    else cout<<2<<'\n';        return 0;}