#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n,k;    cin>>n>>k;    vector<ll>arr(n);    f(n,arr);    ll sum=0;    for (ll i = 0; i < k; i++)sum+=arr[i];    ll total=sum;    ll left=0,right=k;    while (right<n)    {        sum+=arr[right];        sum-=arr[left];        total+=sum;        left++,right++;    }    cout<<fixed<<setprecision(9)<<1.0*total/(n-k+1)<<'\n';    } int main(){ios::sync_with_stdio(0);cin.tie(0);    solve();      return 0;}