#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(int n,vector<int>&a){    for(int i=0;i<n;i++)cin>>a[i];}int red=0,yellow=0;void dfs(int node,int color,vector<vector<int>>&adj,vector<bool>&vis){    vis[node]=1;    if(color==1)yellow++;    else red++;     for(int neighbour:adj[node]){        if(!vis[neighbour])dfs(neighbour,!color,adj,vis);    }}void solve(){    int n;    cin>>n;    vector<vector<int>>adj(n);    for (int i = 0; i<n-1; i++)    {        int u,v;        cin>>u>>v;        u--,v--;        adj[u].push_back(v);        adj[v].push_back(u);    }    vector<bool>vis(n,0);    dfs(0,0,adj,vis);    cout<<1ll*red*yellow-(n-1)<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);solve();      return 0;}