#include<bits/stdc++.h>using namespace std;typedef long long ll;int find(vector<ll>&lucky,ll N){    int l=0,r=lucky.size()-1,ans=lucky.size();    while (l<=r)    {        int mid=l+(r-l)/2;        if(lucky[mid]>=N){            ans=mid;            r=mid-1;        }        else l=mid+1;    }    return ans;}void solve(){    string n;    cin>>n;    ll N=stoll(n);    int len=n.size();     if(len%2==0){        int half=len/2;        vector<ll>lucky;        string s(half,'4');        s+=string(half,'7');        lucky.push_back(stoll(s));        while (next_permutation(s.begin(),s.end()))            lucky.push_back(stoll(s));                sort(begin(lucky),end(lucky));        int k = find(lucky,N);        if(k!=lucky.size()){            cout<<lucky[k]<<'\n';            return;        }    }    int nextlen = (len&1?len+1:len+2);    int half=nextlen/2;    cout<<string(half,'4')<<string(half,'7')<<'\n';    } int main(){ios::sync_with_stdio(0);cin.tie(0);     solve();      return 0;}