#include<bits/stdc++.h>using namespace std;typedef long long ll;// int find(vector<ll>&lucky,ll N){//     int l=0,r=lucky.size()-1,ans=lucky.size();//     while (l<=r)//     {//         int mid=l+(r-l)/2;//         if(lucky[mid]>=N){//             ans=mid;//             r=mid-1;//         }//         else l=mid+1;//     }//     return ans;// }// void solve(){//     string n;//     cin>>n;//     ll N=stoll(n);//     int len=n.size(); //     if(len%2==0){//         int half=len/2;//         vector<ll>lucky;//         string s(half,'4');//         s+=string(half,'7');//         lucky.push_back(stoll(s));//         while (next_permutation(s.begin(),s.end()))//             lucky.push_back(stoll(s));        //         sort(begin(lucky),end(lucky));//         int k = find(lucky,N);//         if(k!=lucky.size()){//             cout<<lucky[k]<<'\n';//             return;//         }//     }//     int nextlen = (len&1?len+1:len+2);//     int half=nextlen/2;//     cout<<string(half,'4')<<string(half,'7')<<'\n'; // } // uisng bitmaskvoid solve(){    string s;    cin>>s;    ll n=stoll(s);    int len=s.length();    if(len&1)len++;    ll ans=LLONG_MAX;     for (int i = len; i<=18; i+=2)    {        int half=i/2;        for (int mask=0; mask <(1<<i); mask++)        {            if(__builtin_popcount(mask)!=half)continue;            ll val=0;            for (int j =0; j<i; j++)            {                val*=10;                if(mask&(1<<j))val+=7;                else val+=4;            }            if(val>=n)ans=min(ans,val);        }        if(ans!=LLONG_MAX)break;    }    cout<<ans<<'\n'; }int main(){ios::sync_with_stdio(0);cin.tie(0);     solve();      return 0;}