#include<bits/stdc++.h>using namespace std;typedef long long ll;void f(ll n,vector<ll>&a){    for(ll i=0;i<n;i++)cin>>a[i];}void solve(){    ll n;    cin>>n;    unordered_map<ll,ll>mp;    for (ll i = 0; i < n; i++){        ll a;        cin>>a;        mp[a]++;    }    ll start_pos;    for(auto it=mp.begin();it!=mp.end();it++){        ll a=it->first;        bool f=false;         if(mp.find(a*2)!=mp.end())f=true;        if(a%3==0 && mp.find(a/3)!=mp.end())f=true;         if(!f){            start_pos=a;            break;        }    }    vector<ll>res;    while (mp.find(start_pos)!=mp.end() && mp[start_pos]>0)    {        res.push_back(start_pos);        mp[start_pos]--;        if(start_pos%2==0 &&  mp.find(start_pos/2)!=mp.end() && mp[start_pos/2]>0)start_pos/=2;        else{            start_pos*=3;        }    }    reverse(begin(res),end(res));    for(ll i:res)cout<<i<<" ";    cout<<'\n';} int main(){ios::sync_with_stdio(0);cin.tie(0);    solve();      return 0;}